  border-top: 1px solid #eee;
}

.pagination a {
  color: inherit;
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.875rem;
  font-weight: 500;
  transition: color 0.2s;
}

.pagination a:hover {
  color: var(--echo-orange);
}

.pagination .prev::before {
  content: "";
  font-size: 1.25rem;
}

.pagination .next::after {
  content: "";
  font-size: 1.25rem;
}

h1 {
  font-size: 2.5rem;
  font-weight: 500;
  margin: 0 0 1.5rem;
  color: var(--dark-bg);
}

h2 {
  font-size: 1.75rem;
  font-weight: 500;
  margin: 2.5rem 0 1rem;
  color: var(--dark-bg);
}

h3 {
  font-size: 1.25rem;
  font-weight: 500;
  margin: 2rem 0 0.75rem;
  color: var(--dark-bg);
}

p {
  font-size: 1rem;
  line-height: 1.6;
  color: #666;
  margin: 0 0 1rem;
  max-width: 720px;
}

:root {
  --echo-orange: #ff4d00;
  --echo-blue: #4b6bfb;
}

.hero {
  background-color: var(--echo-orange);
  padding: 4rem 2rem;
  margin: -2rem -2rem 2rem -2rem;
  color: white;
}

.hero h1 {
  font-size: 3rem;
  line-height: 1.2;
  margin: 0;
}

.card-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  margin: 2rem 0;
}

.card {
  padding: 2rem;
  border-radius: 8px;
  background: #f5f5f5;
  text-decoration: none;
  color: inherit;
  transition: transform 0.2s;
}

.card:hover {
  transform: translateY(-4px);
}

.card img {
  width: 100%;
  height: auto;
  margin-bottom: 1rem;
}

.echo-banner {
  background: var(--echo-blue);
  color: white;
  padding: 4rem 2rem;
  margin: 3rem -2rem;
  text-align: center;
}

.echo-banner h2 {
  font-size: 2.5rem;
  line-height: 1.2;
  margin: 0;
}

.goals {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  margin: 2rem 0;
}

.goal {
  padding: 1.5rem;
  background: #f5f5f5;
  border-radius: 8px;
}

.footer-links {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 2rem;
  margin-top: 4rem;
  padding-top: 2rem;
  border-top: 1px solid #eee;
}

.link {
  color: var(--echo-orange);
  text-decoration: none;
  font-weight: 500;
}

.link:hover {
  text-decoration: underline;
}

@media (max-width: 768px) {
  .card-grid, .goals {
    grid-template-columns: 1fr;
  }

  .footer-links {
    grid-template-columns: 1fr;
  }
}
